			.stream_write,
			.stream_read {
				stroke-width: 12px;
				stroke-dasharray: 10 250;
				stroke-dashoffset: 270;
			}

			.stream_update {
				stroke: #9685ce;
				stroke-width: 6px;
				stroke-dasharray: 15 150;
				stroke-dashoffset: 0;
				opacity: 0.25;
			}

			.stream_read {
				stroke: #9685ce;
			}

			.stream_write.play,
			.stream_read.play {
				opacity: 1;
				animation: stream_write_keyframes 400ms ease-out;
			}

			@keyframes stream_write_keyframes {
				0% {
					stroke-dashoffset: 270;
					stroke-dasharray: 10 250;
				}

				50% {
					stroke-dasharray: 100 160;
				}

				100% {
					stroke-dashoffset: 50;
					stroke-dasharray: 10 250;
				}
			}

			.stream_update.play {
				animation: stream_update_keyframes 2s ease-out;
			}

			@keyframes stream_update_keyframes {
				0% {
					stroke-dashoffset: -120;
					opacity: 1;
				}

				20% {
					opacity: 1;
					stroke-dashoffset: 0;
				}

