			.stream_gear.play {
				animation: stream_gear_keyframes 400ms ease-out;
			}

			@keyframes stream_gear_keyframes {
				0% {
					transform: rotate(0deg);
				}

				100% {
					transform: rotate(90deg);
				}
			}

			.stream_engine.play .stream_icon {
				animation: stream_engine_keyframes 500ms ease-out;
			}

			@keyframes stream_engine_keyframes {
				0% {
					transform: rotate(0deg);
				}

				15% {
					transform: rotate(3deg) scale(1.1);
				}

				30% {
					transform: rotate(-3deg) scale(1.1);
				}

