	<script type="text/javascript">
    <![CDATA[
    (function() {
const diagram = document.currentScript.parentElement;
const writeButton = diagram.querySelector('.stream_animateWrite');
const readButton = diagram.querySelector('.stream_animateRead');
const writes = diagram.querySelectorAll('.stream_write');
const reads =  diagram.querySelectorAll('.stream_read');
const updates = diagram.querySelectorAll('.stream_update');
const gear = diagram.querySelector('.stream_gear');
const engine = diagram.querySelector('.stream_engine');
const step_duration = 400;
const writeLabel = diagram.querySelector('.write_label');
const readLabel = diagram.querySelector('.read_label');

let play, clear;

var resetPlay = function() {
    clearTimeout(play);
    var playing = document.querySelectorAll('.play');
    playing.forEach(el => {
        el.classList.remove('play');
    });
}

const animateWrite = function () {
    writeButton.classList.add('active');
    setTimeout(function(){writeButton.classList.remove('active');}, step_duration*2);
    resetPlay();
    setTimeout(function(){
        writes[Math.floor(Math.random() * writes.length)].classList.add('play');
        play = setTimeout(function(){
            resetPlay();
            engine.classList.add('play');
            gear.classList.add('play');
            setTimeout(function(){
                resetPlay();
                updates[Math.floor(Math.random() * updates.length)].classList.add('play');
                updates[Math.floor(Math.random() * updates.length)].classList.add('play');
                updates[Math.floor(Math.random() * updates.length)].classList.add('play');
            }, step_duration)
        }, step_duration / 2);
    }, 1);
};

const animateRead = function () {
    readButton.classList.add('active');
    setTimeout(function(){readButton.classList.remove('active');}, 250);
    resetPlay();
    setTimeout(function(){
        reads[Math.floor(Math.random() * reads.length)].classList.add('play');
        play = setTimeout(function(){
            resetPlay();
        }, step_duration);
    }, 1);
};

writeButton.addEventListener('click', animateWrite);
readButton.addEventListener('click', animateRead);

let callback = (entries, observer) => {
  if(entries[0].intersectionRatio > 0.9) {
  console.log('stream visible');
  console.log(entries);
    setTimeout(function() {
      writeLabel.classList.remove('hidden');
      animateWrite();
      setTimeout(function() {writeLabel.classList.add('hidden');}, 3500)
    }, 500);
    setTimeout(function() {
      readLabel.classList.remove('hidden');
      setTimeout(function() {readLabel.classList.add('hidden');}, 3500)
      animateRead();
    }, 5000);
    observer.unobserve(diagram);
  }
};

let observer = new IntersectionObserver(callback, {
  root: null,
  rootMargin: '0px',
  threshold: 1.0
});

observer.observe(diagram);

})();
    ]]>
	</script>
