	<script type="text/javascript">
<![CDATA[
(function() {
const diagram = document.currentScript.parentElement;
const writeButton = diagram.querySelector('.trad_animateWrite');
const readButton = diagram.querySelector('.trad_animateRead');
console.log(writeButton);
const writes = diagram.querySelectorAll('.trad_write');
const reads =  diagram.querySelectorAll('.trad_read');
const updates = diagram.querySelectorAll('.trad_update');
const output = diagram.querySelector('.trad_output');
const gear = diagram.querySelector('.trad_gear');
const engine = diagram.querySelector('.trad_engine');
const step_duration = 400;
const readLabel = diagram.querySelector('.read_label');
const writeLabel = diagram.querySelector('.write_label');

let play, clear;

var resetPlay = function() {
    clearTimeout(play);
    var playing = diagram.querySelectorAll('.play');
    playing.forEach(el => {
        el.classList.remove('play');
    });
}

const animateRead = function () {
    readButton.classList.add('active');
    setTimeout(function(){readButton.classList.remove('active');}, step_duration*6);
    resetPlay();
    setTimeout(function(){
        resetPlay();
        engine.classList.add('play');
        gear.classList.add('play');
        play = setTimeout(function(){
            updates[0].classList.add('play');
            updates[1].classList.add('play');
            updates[2].classList.add('play');
            updates[3].classList.add('play');
            updates[4].classList.add('play');

            setTimeout(function(){
                updates[5].classList.add('play');
                updates[6].classList.add('play');
                updates[7].classList.add('play');
                updates[8].classList.add('play');
                updates[9].classList.add('play');
            }, step_duration * 0.5);

            setTimeout(function(){
                updates[10].classList.add('play');
                updates[11].classList.add('play');
                updates[12].classList.add('play');
                updates[13].classList.add('play');
                updates[14].classList.add('play');
            }, step_duration);

            setTimeout(function(){
                updates[15].classList.add('play');
                updates[16].classList.add('play');
                updates[17].classList.add('play');
            }, step_duration * 1.5);

            setTimeout(function(){
                updates[18].classList.add('play');
                updates[19].classList.add('play');
                updates[20].classList.add('play');
            }, step_duration * 2);

            setTimeout(function(){
                updates[21].classList.add('play');
                updates[22].classList.add('play');
                updates[23].classList.add('play');
            }, step_duration * 2.5);

            setTimeout(function(){
                updates[24].classList.add('play');
                updates[25].classList.add('play');
                updates[26].classList.add('play');
            }, step_duration * 3);

            setTimeout(function(){
                updates[27].classList.add('play');
                updates[28].classList.add('play');
                updates[29].classList.add('play');
            }, step_duration * 3.5);

            setTimeout(function(){
                updates[30].classList.add('play');
                updates[31].classList.add('play');
                updates[32].classList.add('play');
            }, step_duration * 4);

            setTimeout(function(){
                output.classList.add('play');
            }, step_duration * 5);

            setTimeout(function() {
                resetPlay();
            }, step_duration*6);
        }, step_duration)
    }, 1);
};

const animateWrite = function () {
    writeButton.classList.add('active');
    setTimeout(function(){writeButton.classList.remove('active');}, 250);
    const write = writes[Math.floor(Math.random() * writes.length)];
    write.classList.remove('play');
    setTimeout(function() {
    write.classList.add('play');
    setTimeout(function(){
      write.classList.remove('play');
    }, 2000);
    }, 1);
};
writeButton.addEventListener('click', animateWrite);
readButton.addEventListener('click', animateRead);

let callback = (entries, observer) => {
  if(entries[0].intersectionRatio > 0.9) {
  console.log('stream visible');
  console.log(entries);
  setTimeout(function() {
    animateWrite();
    writeLabel.classList.remove('hidden');
    setTimeout(function(){ writeLabel.classList.add('hidden'); }, 3500)
  }, 500);
  setTimeout(function() {
    animateRead();
    readLabel.classList.remove('hidden');
    setTimeout(function(){ readLabel.classList.add('hidden'); }, 3500)
  }, 4500);
  observer.unobserve(diagram);
  }
};

let observer = new IntersectionObserver(callback, {
  root: null,
  rootMargin: '0px',
  threshold: 1.0
});

observer.observe(diagram);

